  `ex1_mp3` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Ex1. VR MP3 File',
  `ex1_mp4` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Ex1. Non-VR MP4 File',
  `ex2_mp3` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Ex2. VR MP3 File',
  `ex2_mp4` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL COMMENT 'Ex2. Non-VR MP4 File',
